<?phpclass WebUser extends CWebUser {    private $_model = null;    public $allowAutoLogin = true;    public $authTimeout = 1000000;    private function getModel()	{		if (!$this->isGuest && $this->_model === null)		{			$this->_model = User::model()->findByPk($this->id);		}        return $this->_model;    }		public function getRole()	{            if($user = $this->getModel())            {                return $user->role;            }	}                public function getRoleName()	{            if($user = $this->getModel())            {                return $user->getRoleName();            }	}	public function getName()	{		if($user = $this->getModel())		{            return $user->name;        }	}		        public function getEmail()	{		if($user = $this->getModel())		{                    return $user->email;                }	}                         public function getLogin()	{		if($user = $this->getModel())		{                    return $user->login;                }	}                        }?>